let {name,description} = extractNameAndDescription(prod.norm)

    showObj.set("name",name);
    showObj.set("description",description);
    showObj.set("gwp",prod.gwp);
    showObj.set("declaredWeight",prod.declaredWeight?.declaredWeight||"Not exists");
    showObj.set("weightUnit",prod.declaredWeight?.weightUnit||"Not exists");
    showObj.set("functionalUnit",prod.declaredWeight?.functionalUnit||"Not exists");
    showObj.set("pdf",prod.pdf);
    showObj.set("group",prod.group);
    showObj.set("ida",prod.id);

    array.push(showObj);
}


function extractNameAndDescription(text) {
    // Initialize variables to store name and description
    let name = '';
    let description = '';
    
    // Find the index of "Name:" and "Description:"
    const nameIndex = text?.indexOf("Name:");
    const descriptionIndex = text?.indexOf("Description:");
    
    // Extract name if "Name:" exists
    if (nameIndex !== -1) {
        const nameStartIndex = nameIndex + "Name:".length;
        const nextLabelIndex = descriptionIndex !== -1 ? descriptionIndex : text.length;
        name = text?.substring(nameStartIndex, nextLabelIndex).trim();
    }
    
    // Extract description if "Description:" exists
    if (descriptionIndex !== -1) {
        const descriptionStartIndex = descriptionIndex + "Description:".length;
        description = text?.substring(descriptionStartIndex).trim();
    }
    
    // Return an object containing the extracted name and description
    return {
        name: name,
        description: description
    };
}

